import{_ as e,c as a,ag as i,o as l}from"./chunks/framework.BxW8Wou5.js";const c=JSON.parse('{"title":"3. GET和POST的区别","description":"","frontmatter":{},"headers":[],"relativePath":"interview/网络/3.get和post的区别.md","filePath":"interview/网络/3.get和post的区别.md","lastUpdated":1705937785000}'),r={name:"interview/网络/3.get和post的区别.md"};function o(s,t,T,_,n,d){return l(),a("div",null,t[0]||(t[0]=[i('<h1 id="_3-get和post的区别" tabindex="-1">3. GET和POST的区别 <a class="header-anchor" href="#_3-get和post的区别" aria-label="Permalink to &quot;3. GET和POST的区别&quot;">​</a></h1><h2 id="浅显" tabindex="-1">浅显 <a class="header-anchor" href="#浅显" aria-label="Permalink to &quot;浅显&quot;">​</a></h2><ul><li>GET用来获取数据，POST用来提交数据</li><li>GET参数有长度限制（受限于URL长度），而POST长度无限制</li><li>GET请求会把数据附加在URL之后，用?分割，多个参数用&amp;链接；而POST请求会把请求的数据放在http请求体中。</li><li>GET是明文传输，POST是放在请求体中，但是开发者可以通过抓包工具看到，也相当于是明文的。</li><li>gei请求会保存在浏览器历史记录中，还能保存在web服务器的日志中</li></ul><h2 id="深入" tabindex="-1">深入 <a class="header-anchor" href="#深入" aria-label="Permalink to &quot;深入&quot;">​</a></h2><ul><li>GET请求会把数据附加在URL之后，用?分割，多个参数用&amp;链接；而POST请求会把请求的数据放在http请求体中。 <ul><li>浏览器回退表现不同：GET回退时无害，POST会再次提交请求</li><li>GET产生的URL地址可以被书签保存，而POST不可以</li><li>GET请求只能进行url编码，而POST支持多种编码方式。</li><li>GET请求参数会被完整保留在浏览器历史记录里，而POST中的参数不会被保留。</li></ul></li></ul>',5)]))}const h=e(r,[["render",o]]);export{c as __pageData,h as default};
