import{_ as i,c as a,ag as e,o as r}from"./chunks/framework.BxW8Wou5.js";const t="/blog/picture/%E8%BD%AF%E8%80%83/SysML/%E9%9C%80%E6%B1%82%E5%9B%BE.png",s="/blog/picture/%E8%BD%AF%E8%80%83/SysML/%E9%9C%80%E6%B1%82%E8%B7%9F%E8%B8%AA.png",_=JSON.parse('{"title":"3、SysML（系统建模语言）","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/系统架构师/第3章 - 软件工程/3.SysMl.md","filePath":"ruankao/系统架构师/第3章 - 软件工程/3.SysMl.md","lastUpdated":null}'),o={name:"ruankao/系统架构师/第3章 - 软件工程/3.SysMl.md"};function n(h,l,d,u,c,p){return r(),a("div",null,l[0]||(l[0]=[e('<h1 id="_3、sysml-系统建模语言" tabindex="-1">3、SysML（系统建模语言） <a class="header-anchor" href="#_3、sysml-系统建模语言" aria-label="Permalink to &quot;3、SysML（系统建模语言）&quot;">​</a></h1><h2 id="mbse-sysml-的起源" tabindex="-1">MBSE(SysML 的起源) <a class="header-anchor" href="#mbse-sysml-的起源" aria-label="Permalink to &quot;MBSE(SysML 的起源)&quot;">​</a></h2><p><strong>Model Based Systems Engineering，基于模型的系统工程（大于软件工程）</strong></p><ul><li>建模语言：沟通标准化，MBSE 的核心。【SysMl】 对应软件工程 UML</li><li>建模方法：类似于路线图，【INCONSE 面向对象系统工程法】 对应软件工程面向对象开发</li><li>建模工具：【Agilian,Modelio】 对应软件工程 Visio、Rose</li></ul><h2 id="需求图" tabindex="-1">需求图 <a class="header-anchor" href="#需求图" aria-label="Permalink to &quot;需求图&quot;">​</a></h2><img src="'+t+'"><h3 id="需求关系" tabindex="-1">需求关系 <a class="header-anchor" href="#需求关系" aria-label="Permalink to &quot;需求关系&quot;">​</a></h3><ul><li>包含： 需求可以且只能包含其他需求、</li><li>追踪： 对提供方元素（位于箭头端）的修改可能会导致对客户端元素（位于尾端）修改的需要、</li><li>继承需求： 一个需求可以继承另一个需求的属性、</li><li>改善： 表示一个需求改进了另一个需求的满足程度（对需求进行了细化）、</li><li>满足： 一般是模块满足某种需求、</li><li>验证：表示一个需求验证了另一个需求的正确性</li><li>复制： 表示一个需求复制了另一个需求的特性。</li></ul><h3 id="需求定义" tabindex="-1">需求定义 <a class="header-anchor" href="#需求定义" aria-label="Permalink to &quot;需求定义&quot;">​</a></h3><ul><li><p>严格定义法</p><ul><li>所有的需求都能够被预先定义</li><li>开发人员与用户之间能够准确而清晰的交流</li><li>采用图形/文字可以充分体现最终系统</li></ul></li><li><p>原型法</p><ul><li>并非所有的需求都能在开发前被准确的说明</li><li>项目参加者之间通常都存在交流上的困难</li><li>需要实际的、可供用户参与的系统模型</li><li>有合适的系统开发环境</li><li>反复是完全需要和值得提倡的，需求一旦确定，就应遵从严格的方法</li></ul></li></ul><h3 id="需求验证" tabindex="-1">需求验证 <a class="header-anchor" href="#需求验证" aria-label="Permalink to &quot;需求验证&quot;">​</a></h3><p>已经完成需求规格说明书（SRS），召集客户方来完成需求评审、需求测试</p><p>依据需求描述，设计场景，来判断当前系统是否可以符合要求。</p><p>符合后 1️⃣ 用户签字确认 2️⃣ 把需求标准作为验收标准之一</p><h3 id="需求跟踪" tabindex="-1">需求跟踪 <a class="header-anchor" href="#需求跟踪" aria-label="Permalink to &quot;需求跟踪&quot;">​</a></h3><img src="'+s+'"><h3 id="需求变更管理" tabindex="-1">需求变更管理 <a class="header-anchor" href="#需求变更管理" aria-label="Permalink to &quot;需求变更管理&quot;">​</a></h3><p>变更无法避免，也不能贸然做变更</p><ul><li>1️⃣ 识别出问题</li><li>2️⃣ 问题分析和变更描述</li><li>3️⃣ 变更分析和成本计算</li><li>4️⃣ 变更实现</li></ul><h4 id="十大步骤" tabindex="-1"><strong>十大步骤</strong> <a class="header-anchor" href="#十大步骤" aria-label="Permalink to &quot;**十大步骤**&quot;">​</a></h4><ul><li>明确问题</li><li>书面申请</li><li>判断变更需求类别</li><li>评估变更影响</li><li>判断变更的紧急级别</li><li>沟通确认</li><li>明确解决方案</li><li>审批管理</li><li>执行变更</li><li>版本控制</li></ul>',21)]))}const S=i(o,[["render",n]]);export{_ as __pageData,S as default};
