import{_ as t,c as e,ag as r,o}from"./chunks/framework.BxW8Wou5.js";const n="/blog/picture/%E8%BD%AF%E8%80%83/%E8%BF%9B%E5%BA%A6%E7%AE%A1%E7%90%86.png",s="/blog/picture/%E8%BD%AF%E8%80%83/WBS%E5%B7%A5%E4%BD%9C%E5%88%86%E8%A7%A3%E7%BB%93%E6%9E%84.png",i="/blog/picture/%E8%BD%AF%E8%80%83/PDM%E5%9B%BE.png",p="/blog/picture/%E8%BD%AF%E8%80%83/%E7%94%98%E7%89%B9%E5%9B%BE.png",B=JSON.parse('{"title":"2、项目进度管理","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/系统架构师/第4章 - 项目管理/2.项目进度管理.md","filePath":"ruankao/系统架构师/第4章 - 项目管理/2.项目进度管理.md","lastUpdated":null}'),_={name:"ruankao/系统架构师/第4章 - 项目管理/2.项目进度管理.md"};function l(c,a,d,E,h,u){return o(),e("div",null,a[0]||(a[0]=[r('<h1 id="_2、项目进度管理" tabindex="-1">2、项目进度管理 <a class="header-anchor" href="#_2、项目进度管理" aria-label="Permalink to &quot;2、项目进度管理&quot;">​</a></h1><p>为了确保项目按期完成所需要的管理过程</p><img src="'+n+'"><h2 id="wbs-工作分解结构" tabindex="-1">WBS 工作分解结构 <a class="header-anchor" href="#wbs-工作分解结构" aria-label="Permalink to &quot;WBS 工作分解结构&quot;">​</a></h2><img src="'+s+'"><p>功能模块是一个工作包（拆解的比较细的）</p><h2 id="三点估算法" tabindex="-1">三点估算法 <a class="header-anchor" href="#三点估算法" aria-label="Permalink to &quot;三点估算法&quot;">​</a></h2><p>（乐观时间 + 4 * 最可能时间 + 悲观时间） / 6</p><h2 id="关键路径法" tabindex="-1">关键路径法 <a class="header-anchor" href="#关键路径法" aria-label="Permalink to &quot;关键路径法&quot;">​</a></h2><p>关键路径法是在制定进度计划时使用的一种进度网络分析技术。关键路径法沿着项目进度网络路线进行正向和反向分析，从而计算出所有计划活动理论上的最早开始与完成日期、最迟开始与完成日期，不考虑任何资源限制。</p><p>【总时差即松弛时间】：在不延误总工期的前提下，该活动的机动时间。<strong>活动的总时差等于该活动最迟完成时间与最早完成时间之差</strong>，或该活动最迟开始时间与最早开始时间之差。</p><p>对于网络计划中以终点为完成节点的活动，其自由时差与总时差相等。此外，由于活动的自由时差是其总时差的构成部分，所以，当活动的总时差为零时，其自由时差必然为零，可不必进行专门计算。</p><p>单代号网络图： <img src="'+i+'"></p><p>关键路径是最长路径</p><h2 id="甘特图" tabindex="-1">甘特图 <a class="header-anchor" href="#甘特图" aria-label="Permalink to &quot;甘特图&quot;">​</a></h2><img src="'+p+'">',16)]))}const b=t(_,[["render",l]]);export{B as __pageData,b as default};
