import{_ as l,c as i,ag as e,o as r}from"./chunks/framework.BxW8Wou5.js";const t="/blog/picture/%E8%BD%AF%E8%80%83/CMMI.png",p=JSON.parse('{"title":"3、软件质量管理","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/系统架构师/第4章 - 项目管理/3.软件质量管理.md","filePath":"ruankao/系统架构师/第4章 - 项目管理/3.软件质量管理.md","lastUpdated":1746770091000}'),o={name:"ruankao/系统架构师/第4章 - 项目管理/3.软件质量管理.md"};function n(h,a,s,d,u,c){return r(),i("div",null,a[0]||(a[0]=[e('<h1 id="_3、软件质量管理" tabindex="-1">3、软件质量管理 <a class="header-anchor" href="#_3、软件质量管理" aria-label="Permalink to &quot;3、软件质量管理&quot;">​</a></h1><h2 id="影响软件质量的-3-组因素" tabindex="-1">影响软件质量的 3 组因素 <a class="header-anchor" href="#影响软件质量的-3-组因素" aria-label="Permalink to &quot;影响软件质量的 3 组因素&quot;">​</a></h2><h3 id="产品修改" tabindex="-1">产品修改 <a class="header-anchor" href="#产品修改" aria-label="Permalink to &quot;产品修改&quot;">​</a></h3><ul><li>可理解性（我能理解它吗）</li><li>可维修性（我能修复它吗）</li><li>灵活性（我能改变它吗）</li><li>可测试性（我能测试它吗）</li></ul><h3 id="产品转移" tabindex="-1">产品转移 <a class="header-anchor" href="#产品转移" aria-label="Permalink to &quot;产品转移&quot;">​</a></h3><ul><li>可移植性（我能在另一台机器上使用它吗）</li><li>可再用性（我能再用它的某些部分吗）</li><li>互运行性（我能把它和另一个系统结合吗）</li></ul><h3 id="产品运行" tabindex="-1">产品运行 <a class="header-anchor" href="#产品运行" aria-label="Permalink to &quot;产品运行&quot;">​</a></h3><ul><li>正确性（它按我的需要工作吗）</li><li>健壮性（对意外环境它能适当地响应吗）</li><li>效率（完成预定功能时它需要的计算机资源多吗）</li><li>完整性（它是安全的吗）</li><li>可用性（我能使用它吗）</li><li>风险（能按预定计划完成它吗）</li></ul><h2 id="软件质量控制和质量保证" tabindex="-1">软件质量控制和质量保证 <a class="header-anchor" href="#软件质量控制和质量保证" aria-label="Permalink to &quot;软件质量控制和质量保证&quot;">​</a></h2><ul><li><p>质量保证（QA）一般是每隔一定时间（例如：每个阶段末）进行的，主要通过系统的质量审计和过程分析来保证项目的质量。独特工具包括：<strong>质量审计和过程分析</strong></p></li><li><p>质量控制（QC）是实时监控项目的具体结果，以判断它们是否符合相关质量标准，制定有效方案，以消除产生质量问题的原因</p></li></ul><h3 id="质量保证的主要目标" tabindex="-1">质量保证的主要目标 <a class="header-anchor" href="#质量保证的主要目标" aria-label="Permalink to &quot;质量保证的主要目标&quot;">​</a></h3><ul><li>【事前预防】工作。</li><li>尽量在刚刚引入缺陷时即将其捕获，而不是让缺陷扩散到下一个阶段</li><li>作用于【过程】而【不是最终产品】</li><li>贯穿于【所有的活动之中】，而不是只集中于一点</li></ul><h2 id="软件能力成熟度模型集成-cmmi" tabindex="-1">软件能力成熟度模型集成（CMMI） <a class="header-anchor" href="#软件能力成熟度模型集成-cmmi" aria-label="Permalink to &quot;软件能力成熟度模型集成（CMMI）&quot;">​</a></h2><p>在软件开发机构中被广泛用来指导软件过程改进</p><p>国外用的比较多</p><p>分为阶段式和连续式</p><img src="'+t+'">',17)]))}const m=l(o,[["render",n]]);export{p as __pageData,m as default};
