import{_ as e,c as l,ag as i,o as r}from"./chunks/framework.BxW8Wou5.js";const t="/blog/picture/%E8%BD%AF%E8%80%83/DSSA.png",o="/blog/picture/%E8%BD%AF%E8%80%83/DSSA%E5%BB%BA%E7%AB%8B%E8%BF%87%E7%A8%8B.png",h="/blog/picture/%E8%BD%AF%E8%80%83/DSSA%E4%B8%89%E5%B1%82%E6%AC%A1%E6%A8%A1%E5%9E%8B.png",p=JSON.parse('{"title":"6.特定领域软件架构 DSSA","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/系统架构师/第5章 - 软件架构设计/6.特定领域软件架构DSSA.md","filePath":"ruankao/系统架构师/第5章 - 软件架构设计/6.特定领域软件架构DSSA.md","lastUpdated":1747028136000}'),n={name:"ruankao/系统架构师/第5章 - 软件架构设计/6.特定领域软件架构DSSA.md"};function s(d,a,c,u,b,q){return r(),l("div",null,a[0]||(a[0]=[i('<h1 id="_6-特定领域软件架构-dssa" tabindex="-1">6.特定领域软件架构 DSSA <a class="header-anchor" href="#_6-特定领域软件架构-dssa" aria-label="Permalink to &quot;6.特定领域软件架构 DSSA&quot;">​</a></h1><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>特定领域软件架构以一个特定问题领域为对象，形成由<strong>领域参考模型、参考需求、参考架构</strong>等组成的开发基础架构，支持一个特定领域中<strong>多个应用的生成</strong></p><img src="'+t+'"><h2 id="参与人员" tabindex="-1">参与人员 <a class="header-anchor" href="#参与人员" aria-label="Permalink to &quot;参与人员&quot;">​</a></h2><h3 id="领域专家" tabindex="-1">领域专家 <a class="header-anchor" href="#领域专家" aria-label="Permalink to &quot;领域专家&quot;">​</a></h3><h4 id="角色" tabindex="-1">角色 <a class="header-anchor" href="#角色" aria-label="Permalink to &quot;角色&quot;">​</a></h4><p>有经验的用户、从事该领域中系统的需求分析、设计、实现以及项目管理的有经验的软件工程师等，作为特定应用领域知识的权威来源，负责提供该领域深入、专业的知识和见解。</p><h4 id="职责" tabindex="-1">职责 <a class="header-anchor" href="#职责" aria-label="Permalink to &quot;职责&quot;">​</a></h4><ul><li>提供领域相关知识的权威信息</li><li>参与模型的构建和验证，确保模型能准确地反映现实世界的领域知识</li><li>阐述领域内用户需求和业务目标</li><li>评估 DSSA 的适用性和有效性</li><li>在领域特定的建模语言和工具开发过程中提供反馈</li><li>参与基于 DSSA 构建的应用的评审和测试</li></ul><h3 id="领域分析人员" tabindex="-1">领域分析人员 <a class="header-anchor" href="#领域分析人员" aria-label="Permalink to &quot;领域分析人员&quot;">​</a></h3><h4 id="角色-1" tabindex="-1">角色 <a class="header-anchor" href="#角色-1" aria-label="Permalink to &quot;角色&quot;">​</a></h4><p>领域分析人员应由具有知识工程背景的有经验的系统分析员来担任，负责从领域专家和其他相关来源收集、分析、组织和形式化领域知识，并将其转化为软件开发可以理解和使用的模型。 <strong>分析后可以得到模型</strong></p><h4 id="职责-1" tabindex="-1">职责 <a class="header-anchor" href="#职责-1" aria-label="Permalink to &quot;职责&quot;">​</a></h4><ul><li>识别和提取领域内的关键概念、关系、流程和规则。</li><li>构建和维护领域模型，例如概念模型、业务流程模型和规则模型。</li><li>分析领域需求，识别共性和可变性。</li><li>将领域知识转化为软件开发可以理解的形式。</li><li>参与参考架构的设计和可重用构件的识别。</li><li>协助领域专家验证领域模型的准确性。</li></ul><h3 id="领域设计人员" tabindex="-1">领域设计人员 <a class="header-anchor" href="#领域设计人员" aria-label="Permalink to &quot;领域设计人员&quot;">​</a></h3><h4 id="角色-2" tabindex="-1">角色 <a class="header-anchor" href="#角色-2" aria-label="Permalink to &quot;角色&quot;">​</a></h4><p>领域设计人员应由有经验的软件设计人员来担任， 基于领域模型和分析结果，负责设计领域特定的软件架构 (DSSA)，定义参考架构和可重用构件的蓝图。 <strong>设计后可以得到架构</strong></p><h4 id="职责-2" tabindex="-1">职责 <a class="header-anchor" href="#职责-2" aria-label="Permalink to &quot;职责&quot;">​</a></h4><ul><li>架构设计： 设计 DSSA 的整体结构和组织框架（参考架构），包括主要组件、接口、交互模式和架构风格，以满足领域的需求和约束。</li><li>构件识别与定义： 识别领域内具有可重用性的软件构件，并定义它们的接口、功能和行为规范。</li><li>设计原则制定： 制定领域特定的设计原则和指南，确保基于 DSSA 构建的应用具有一致性和高质量。</li><li>技术选型指导： 根据领域特性和需求，指导技术和平台的选择。</li><li>质量属性考虑： 在架构和构件设计中考虑性能、安全性、可维护性等重要的质量属性。</li></ul><h3 id="领域实现人员" tabindex="-1">领域实现人员 <a class="header-anchor" href="#领域实现人员" aria-label="Permalink to &quot;领域实现人员&quot;">​</a></h3><h4 id="角色-3" tabindex="-1">角色 <a class="header-anchor" href="#角色-3" aria-label="Permalink to &quot;角色&quot;">​</a></h4><p>领域实现人员应由有经验的程序设计人员来担任，负责根据领域设计人员的规范和蓝图，开发可重用的软件构件，并使用这些构件构建特定的领域应用程序。·</p><h4 id="职责-3" tabindex="-1">职责 <a class="header-anchor" href="#职责-3" aria-label="Permalink to &quot;职责&quot;">​</a></h4><ul><li>构件开发： 根据构件规范进行详细设计、编码和单元测试，实现可重用的软件模块。</li><li>应用构建： 利用已开发的可重用构件，结合特定的应用需求进行组装、配置和定制，构建最终的领域应用程序。</li><li>集成测试： 对构建的应用进行集成测试，确保各个构件协同工作正常。</li><li>技术实现： 运用合适的编程语言、框架、工具和技术来实现设计方案。</li><li>部署与维护支持： 参与应用程序的部署过程，并为后续的维护工作提供支持。</li></ul><h2 id="建立过程" tabindex="-1">建立过程 <a class="header-anchor" href="#建立过程" aria-label="Permalink to &quot;建立过程&quot;">​</a></h2><img src="'+o+'"><h2 id="三层次模型" tabindex="-1">三层次模型 <a class="header-anchor" href="#三层次模型" aria-label="Permalink to &quot;三层次模型&quot;">​</a></h2><img src="'+h+'"><h3 id="领域开发环境-领域架构师" tabindex="-1">领域开发环境（领域架构师） <a class="header-anchor" href="#领域开发环境-领域架构师" aria-label="Permalink to &quot;领域开发环境（领域架构师）&quot;">​</a></h3><p>用于定义、建模、分析和设计领域特定的概念、规则、流程、组件</p><h3 id="领域特定的应用开发环境-应用开发者" tabindex="-1">领域特定的应用开发环境（应用开发者） <a class="header-anchor" href="#领域特定的应用开发环境-应用开发者" aria-label="Permalink to &quot;领域特定的应用开发环境（应用开发者）&quot;">​</a></h3><p>基于领域模型、参考架构和可重用构件快速构件、配置和定制领域特定的应用程序</p><h3 id="应用执行环境-操作员" tabindex="-1">应用执行环境（操作员） <a class="header-anchor" href="#应用执行环境-操作员" aria-label="Permalink to &quot;应用执行环境（操作员）&quot;">​</a></h3><p>最终用户运行和使用领域特定应用程序的平台和基础设施。</p>',35)]))}const m=e(n,[["render",s]]);export{p as __pageData,m as default};
