import{_ as e,c as t,ag as r,o}from"./chunks/framework.BxW8Wou5.js";const u=JSON.parse('{"title":"4、嵌入式数据库","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/系统架构师/第8章 - 计算机基础知识/2、嵌入式技术/4、嵌入式数据库.md","filePath":"ruankao/系统架构师/第8章 - 计算机基础知识/2、嵌入式技术/4、嵌入式数据库.md","lastUpdated":1748002092000}'),l={name:"ruankao/系统架构师/第8章 - 计算机基础知识/2、嵌入式技术/4、嵌入式数据库.md"};function i(n,a,h,s,d,_){return o(),t("div",null,a[0]||(a[0]=[r('<h1 id="_4、嵌入式数据库" tabindex="-1">4、嵌入式数据库 <a class="header-anchor" href="#_4、嵌入式数据库" aria-label="Permalink to &quot;4、嵌入式数据库&quot;">​</a></h1><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><ul><li>嵌入式：常规的有专门服务器</li><li>实时性：要求及时响应</li><li>移动性：国内提的比较多</li><li>伸缩性：结合软硬件和客户需要进行伸缩</li></ul><h2 id="分类-按照存储位置不同" tabindex="-1">分类（按照存储位置不同） <a class="header-anchor" href="#分类-按照存储位置不同" aria-label="Permalink to &quot;分类（按照存储位置不同）&quot;">​</a></h2><h3 id="基于内存方式" tabindex="-1">基于内存方式 <a class="header-anchor" href="#基于内存方式" aria-label="Permalink to &quot;基于内存方式&quot;">​</a></h3><p>定义：基于内存的数据库系统是实时系统和数据库系统的有机结合 特点：内存数据库是支持实时事务的最佳技术，其本质特诊是以其“主拷贝”或“工作版本”常驻内存，即活动事务只与实时内存数据库的内存拷贝打交道</p><h3 id="基于文件方式" tabindex="-1">基于文件方式 <a class="header-anchor" href="#基于文件方式" aria-label="Permalink to &quot;基于文件方式&quot;">​</a></h3><p>定义：以文件方式存储数据库数据。即数据按照一定格式存储在磁盘中。使用时由应用程序通过相应的驱动程序甚至直接对数据文件进行读写 特点：访问方式是被动的，只要了解文件格式，任何软件都可以直接读取，所以<strong>安全性低</strong>。虽然存在诸多弊端，但是，可以满足嵌入式系统在空间、事件等方面的特殊要求</p><h3 id="基于网络方式" tabindex="-1">基于网络方式 <a class="header-anchor" href="#基于网络方式" aria-label="Permalink to &quot;基于网络方式&quot;">​</a></h3><p>定义：基于手机的 4/5G 网络之上的数据库系统，在逻辑上可以把嵌入式设备看作远程服务器的一个客户端 特点：无需解析 SQL 语句；支持更多 SQL 操作；客户端小、无需支持可剪裁性；有利于代码重用。</p>',10)]))}const p=e(l,[["render",i]]);export{u as __pageData,p as default};
