import{_ as l,c as e,ag as i,o as r}from"./chunks/framework.BxW8Wou5.js";const _=JSON.parse('{"title":"3、网络攻击","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/软件设计师/第12章 - 信息安全/3.网络攻击.md","filePath":"ruankao/软件设计师/第12章 - 信息安全/3.网络攻击.md","lastUpdated":null}'),t={name:"ruankao/软件设计师/第12章 - 信息安全/3.网络攻击.md"};function o(h,a,n,d,u,s){return r(),e("div",null,a[0]||(a[0]=[i('<h1 id="_3、网络攻击" tabindex="-1">3、网络攻击 <a class="header-anchor" href="#_3、网络攻击" aria-label="Permalink to &quot;3、网络攻击&quot;">​</a></h1><h2 id="拒绝服务攻击-ddos-攻击-考的多" tabindex="-1">拒绝服务攻击（DDOS 攻击-考的多） <a class="header-anchor" href="#拒绝服务攻击-ddos-攻击-考的多" aria-label="Permalink to &quot;拒绝服务攻击（DDOS 攻击-考的多）&quot;">​</a></h2><ul><li>目的是使计算机或网络无法提供正常的服务</li><li>是不断向计算机发起请求来实现的</li></ul><h2 id="重放攻击" tabindex="-1">重放攻击 <a class="header-anchor" href="#重放攻击" aria-label="Permalink to &quot;重放攻击&quot;">​</a></h2><ul><li>攻击者发送一个目的主机已经接受过的报文来达到攻击目的</li><li>攻击者利用网络监听或者其他方式盗取认证凭据，之后再重新发送给认证服务器</li><li>主要用于身份认证过程，目的是破坏认证的正确性</li></ul><h2 id="口令入侵攻击-考得少" tabindex="-1">口令入侵攻击（考得少） <a class="header-anchor" href="#口令入侵攻击-考得少" aria-label="Permalink to &quot;口令入侵攻击（考得少）&quot;">​</a></h2><ul><li>使用某些合法用户的账号和口令登陆到目的主机，然后再实施攻击活动</li></ul><h2 id="特洛伊木马" tabindex="-1">特洛伊木马 <a class="header-anchor" href="#特洛伊木马" aria-label="Permalink to &quot;特洛伊木马&quot;">​</a></h2><ul><li>被伪装成程序或游戏，当用户下载带有木马的软件或附件时，这个程序就会向黑客发起连接请求，建立连接后黑客就实施攻击活动</li></ul><h2 id="端口欺骗攻击" tabindex="-1">端口欺骗攻击 <a class="header-anchor" href="#端口欺骗攻击" aria-label="Permalink to &quot;端口欺骗攻击&quot;">​</a></h2><ul><li>采用端口扫描找到系统漏洞从而实施攻击</li></ul><h2 id="网络监听" tabindex="-1">网络监听 <a class="header-anchor" href="#网络监听" aria-label="Permalink to &quot;网络监听&quot;">​</a></h2><ul><li>攻击者可以接受某一网段在同一条物理通道上传输的所有信息</li><li>使用网络监听可以轻松截取包括账号和口令在内的信息资料</li></ul><h2 id="ip-欺骗" tabindex="-1">IP 欺骗 <a class="header-anchor" href="#ip-欺骗" aria-label="Permalink to &quot;IP 欺骗&quot;">​</a></h2><ul><li>产生的 IP 数据包为伪造的源 IP 地址，以便冒充其他系统或发件人的身份</li></ul><h2 id="sql-注入攻击" tabindex="-1">SQL 注入攻击 <a class="header-anchor" href="#sql-注入攻击" aria-label="Permalink to &quot;SQL 注入攻击&quot;">​</a></h2><ul><li>是黑客对数据库进行攻击的常用手段之一</li><li>没有对用户输入数据的合法性进行判断，使应用程序存在安全隐患</li><li>攻击者可以提交一段数据库查询代码，从而获取数据库中的敏感信息</li></ul><h2 id="入侵检测技术" tabindex="-1">入侵检测技术 <a class="header-anchor" href="#入侵检测技术" aria-label="Permalink to &quot;入侵检测技术&quot;">​</a></h2><ul><li>专家系统</li><li>模型检测</li><li>简单匹配</li></ul>',19)]))}const q=l(t,[["render",o]]);export{_ as __pageData,q as default};
