import{_ as l,c as i,ag as e,o as r}from"./chunks/framework.BxW8Wou5.js";const _=JSON.parse('{"title":"1、知识产权","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/软件设计师/第4章 - 知识产权/1.知识产权.md","filePath":"ruankao/软件设计师/第4章 - 知识产权/1.知识产权.md","lastUpdated":1761796671000}'),t={name:"ruankao/软件设计师/第4章 - 知识产权/1.知识产权.md"};function o(h,a,n,d,u,s){return r(),i("div",null,a[0]||(a[0]=[e('<h1 id="_1、知识产权" tabindex="-1">1、知识产权 <a class="header-anchor" href="#_1、知识产权" aria-label="Permalink to &quot;1、知识产权&quot;">​</a></h1><h2 id="著作权" tabindex="-1">著作权 <a class="header-anchor" href="#著作权" aria-label="Permalink to &quot;著作权&quot;">​</a></h2><ul><li>也称为版权，指作者对其创作的作品享有人身权和财产权</li></ul><h3 id="人身权-背" tabindex="-1">人身权（背） <a class="header-anchor" href="#人身权-背" aria-label="Permalink to &quot;人身权（背）&quot;">​</a></h3><ul><li>发表权：时间受限制（作者终身及死亡后 50 年）</li><li>署名权：永远</li><li>修改权：永远</li><li>保护作品完整权：永远</li></ul><h3 id="财产权-受时间限制-作者终身及死后-50-年" tabindex="-1">财产权（受时间限制，作者终身及死后 50 年） <a class="header-anchor" href="#财产权-受时间限制-作者终身及死后-50-年" aria-label="Permalink to &quot;财产权（受时间限制，作者终身及死后 50 年）&quot;">​</a></h3><ul><li>使用权</li><li>获得报偿权</li></ul><h2 id="计算机软件著作权" tabindex="-1">计算机软件著作权 <a class="header-anchor" href="#计算机软件著作权" aria-label="Permalink to &quot;计算机软件著作权&quot;">​</a></h2><h3 id="主体" tabindex="-1">主体 <a class="header-anchor" href="#主体" aria-label="Permalink to &quot;主体&quot;">​</a></h3><ul><li>主体是指享受著作权的人</li><li>根据《中华人民共和国著作权法》和《计算机软件保护条例》</li></ul><h3 id="客体" tabindex="-1">客体 <a class="header-anchor" href="#客体" aria-label="Permalink to &quot;客体&quot;">​</a></h3><ul><li>客体是指被版权保护的作品或作品内容（受保护的对象）</li><li>保护的是计算机程序（语言程序和目标程序）和计算机软件的文档（程序设计说明书、流程图等）</li></ul><h3 id="权利-考得少-自开发完成之日起产生-保护期-50-年" tabindex="-1">权利（考得少）- 自开发完成之日起产生，保护期 50 年 <a class="header-anchor" href="#权利-考得少-自开发完成之日起产生-保护期-50-年" aria-label="Permalink to &quot;权利（考得少）- 自开发完成之日起产生，保护期 50 年&quot;">​</a></h3><h4 id="人身权" tabindex="-1">人身权 <a class="header-anchor" href="#人身权" aria-label="Permalink to &quot;人身权&quot;">​</a></h4><ul><li>发表权</li><li>署名权（开发者身份权）：无时间限制</li></ul><h4 id="财产权" tabindex="-1">财产权 <a class="header-anchor" href="#财产权" aria-label="Permalink to &quot;财产权&quot;">​</a></h4><ul><li>使用权</li><li>复制权</li><li>修改权</li><li>发行权</li><li>翻译权</li></ul><h2 id="知识产权的地域性" tabindex="-1">知识产权的地域性 <a class="header-anchor" href="#知识产权的地域性" aria-label="Permalink to &quot;知识产权的地域性&quot;">​</a></h2><ul><li>知识产权有严格的地域性特点</li><li>各国主管机关依照本国法律授予的知识产权，只能在其本国领域内受法律保护</li><li>例：外国人在我国领域外使用中国专利局授权的发明专利，不侵犯我国专利权</li></ul>',19)]))}const b=l(t,[["render",o]]);export{_ as __pageData,b as default};
