import{_ as e,c as l,ag as t,o}from"./chunks/framework.BxW8Wou5.js";const u=JSON.parse('{"title":"5、视图和索引","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/软件设计师/第5章 - 数据库/5.视图和索引.md","filePath":"ruankao/软件设计师/第5章 - 数据库/5.视图和索引.md","lastUpdated":null}'),r={name:"ruankao/软件设计师/第5章 - 数据库/5.视图和索引.md"};function i(d,a,n,h,c,s){return o(),l("div",null,a[0]||(a[0]=[t('<h1 id="_5、视图和索引" tabindex="-1">5、视图和索引 <a class="header-anchor" href="#_5、视图和索引" aria-label="Permalink to &quot;5、视图和索引&quot;">​</a></h1><h2 id="视图" tabindex="-1">视图 <a class="header-anchor" href="#视图" aria-label="Permalink to &quot;视图&quot;">​</a></h2><ul><li>视图是数据库中的虚拟表，是从一个或者多个基本表或视图中导出的表</li></ul><h3 id="创建视图" tabindex="-1">创建视图 <a class="header-anchor" href="#创建视图" aria-label="Permalink to &quot;创建视图&quot;">​</a></h3><p><code>CREATE VIEW 视图名 AS SELECT 查询子句 [WITH CHECK OPTION]</code></p><ul><li>子查询可以是任意复杂的 SELECT 语句，但不能含有 ORDER BY 和 DISTINCT</li><li>WITH CHECK OPTION 表示对 UPDATE、INSERT、DELETE 操作时保持更新、插入或删除的行满足视图定义中的条件（子查询中的条件表达式-WHERE）</li></ul><h3 id="删除视图" tabindex="-1">删除视图 <a class="header-anchor" href="#删除视图" aria-label="Permalink to &quot;删除视图&quot;">​</a></h3><p><code>DROP VIEW 视图名</code></p><h2 id="索引" tabindex="-1">索引 <a class="header-anchor" href="#索引" aria-label="Permalink to &quot;索引&quot;">​</a></h2><ul><li>与书籍索引类似</li><li>使用索引可以使数据库程序无需对整个表进行扫描，就可以在其中找到所需数据</li><li>在物理空间中添加逻辑指针</li><li>运用数据库的内模式进行创建、修改</li></ul><h3 id="建立索引" tabindex="-1">建立索引 <a class="header-anchor" href="#建立索引" aria-label="Permalink to &quot;建立索引&quot;">​</a></h3><p><code>CREATE INDEX 索引名 ON 表名(字段名)</code></p><h3 id="删除索引" tabindex="-1">删除索引 <a class="header-anchor" href="#删除索引" aria-label="Permalink to &quot;删除索引&quot;">​</a></h3><p><code>DROP INDEX 索引名</code></p>',14)]))}const E=e(r,[["render",i]]);export{u as __pageData,E as default};
