import{_ as e,c as l,ag as t,o as i}from"./chunks/framework.BxW8Wou5.js";const u=JSON.parse('{"title":"9、数据库的备份与恢复","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/软件设计师/第5章 - 数据库/9.备份与恢复.md","filePath":"ruankao/软件设计师/第5章 - 数据库/9.备份与恢复.md","lastUpdated":1761796671000}'),r={name:"ruankao/软件设计师/第5章 - 数据库/9.备份与恢复.md"};function o(n,a,_,s,d,h){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="_9、数据库的备份与恢复" tabindex="-1">9、数据库的备份与恢复 <a class="header-anchor" href="#_9、数据库的备份与恢复" aria-label="Permalink to &quot;9、数据库的备份与恢复&quot;">​</a></h1><h2 id="故障类型" tabindex="-1">故障类型 <a class="header-anchor" href="#故障类型" aria-label="Permalink to &quot;故障类型&quot;">​</a></h2><h2 id="备份方法" tabindex="-1">备份方法 <a class="header-anchor" href="#备份方法" aria-label="Permalink to &quot;备份方法&quot;">​</a></h2><ul><li>基本原理是建立数据冗余（重复存储）</li><li>方法是进行数据转储存 <strong>（转储和事务可以并行执行）</strong></li><li>静态转储：在转储期间不允许对数据库进行存取、修改操作</li><li>动态转储：允许对数据库进行存取、修改操作</li><li>海量转储：每次转储全部数据</li><li>增量转储：每次转储新增数据</li><li>日志文件：把事务开始、结束、对数据库的插入、删除和修改写入日志文件</li></ul><h2 id="恢复方法" tabindex="-1">恢复方法 <a class="header-anchor" href="#恢复方法" aria-label="Permalink to &quot;恢复方法&quot;">​</a></h2><p>有三个步骤：</p><ul><li>反向扫描日志文件：找到事务更新操作</li><li>对事务的更新操作执行逆操作</li><li>继续反向扫描日志文件：找到其他更新操作，进行逆操作，直到找到事务开始</li></ul>',7)]))}const p=e(r,[["render",o]]);export{u as __pageData,p as default};
