import{_ as l,c as e,ag as t,o as i}from"./chunks/framework.BxW8Wou5.js";const c=JSON.parse('{"title":"2、类","description":"","frontmatter":{},"headers":[],"relativePath":"ruankao/软件设计师/第6章 - 面向对象/2.类.md","filePath":"ruankao/软件设计师/第6章 - 面向对象/2.类.md","lastUpdated":null}'),r={name:"ruankao/软件设计师/第6章 - 面向对象/2.类.md"};function o(n,a,s,u,d,h){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="_2、类" tabindex="-1">2、类 <a class="header-anchor" href="#_2、类" aria-label="Permalink to &quot;2、类&quot;">​</a></h1><h2 id="概念" tabindex="-1">概念 <a class="header-anchor" href="#概念" aria-label="Permalink to &quot;概念&quot;">​</a></h2><ul><li>一个类定义了一组大体上相似的对象</li><li>一个类所包含的方法和数据描述一组对象的共同行为和属性</li><li>类是在对象之上的抽象</li><li>对象是类的具体化，是类的实例</li></ul><h2 id="分类" tabindex="-1">分类 <a class="header-anchor" href="#分类" aria-label="Permalink to &quot;分类&quot;">​</a></h2><ul><li>实体类：对象表示真实世界中的实体（人、物）</li><li>接口类（边界类）：为用户提供一种与系统合作交互的方式，分为人和系统两类 <ul><li>人：显示屏、窗口、WEB 窗体、对话框、菜单、列表框、条形码、二维码等</li><li>系统：把数据发送到其他系统/从其他系统接受数据</li></ul></li><li>控制类：用来控制活动流，<strong>充当协调者</strong></li></ul><h2 id="一般、特殊" tabindex="-1">一般、特殊 <a class="header-anchor" href="#一般、特殊" aria-label="Permalink to &quot;一般、特殊&quot;">​</a></h2><p>有些类之间存在一般和特殊关系，<strong>即类是某个类的特殊情况</strong>，某个类是类的一般情况 <strong>一般类是特殊类的父类</strong></p><ul><li>比如交通工具是一般类，汽车、轮船就是特殊类</li></ul><h2 id="方法重载" tabindex="-1">方法重载 <a class="header-anchor" href="#方法重载" aria-label="Permalink to &quot;方法重载&quot;">​</a></h2><ul><li>函数名相同</li><li>参数列表不同（参数个数、顺序、类型不同）</li></ul>',10)]))}const m=l(r,[["render",o]]);export{c as __pageData,m as default};
